const fetchDetails = async (character) =>{

  
    try {
      const request = await fetch(`https://gateway.marvel.com:443/v1/public/characters/`+character.id+`&apikey=c3539cb9d6399d31be99ff2f6b0f6aa1`);
      const response = await request.json();
      console.log(response);
      return response ;
    } catch (error) {
      console.error(error);
      return {msg: error.message};
    }
  };
  
  export default fetchDetails;
